export let GLOBAL_OPTIONS = {
    key: "",
    baseUrl: "https://api.what3words.com/v3"
};
export const searchParams = (data) => Object.keys(data)
    .map(key => `${encodeURIComponent(key)}=${encodeURIComponent(data[key])}`)
    .join("&");
export const coordinatesToString = (coordinates) => `${coordinates.lat},${coordinates.lng}`;
export const boundsToString = (bounds) => `${coordinatesToString(bounds.southwest)},${coordinatesToString(bounds.northeast)}`;
export const arrayToString = (array) => array.join(",");
export const setOptions = (options) => {
    GLOBAL_OPTIONS = Object.assign(Object.assign({}, GLOBAL_OPTIONS), options);
};
export const getOptions = () => GLOBAL_OPTIONS;
export const getPlatform = (platform) => {
    switch (platform) {
        case 'darwin':
            return 'Mac OS X';
        case 'win32':
            return 'Windows';
        case 'linux':
            return 'Linux';
        default:
            return "";
    }
};
